	public boolean validate(T t) {
		try {
			Pattern pattern = Pattern.compile(EMAIL_PATTERN);
			if (t.getClass().getSimpleName().equals(Client.class.getSimpleName())) {
				if (!pattern.matcher(((Client) t).getEmail()).matches()) {
					throw new IllegalArgumentException(" Invalid email!");
				}
			} else
				throw new IllegalArgumentException(
						"Class " + t.getClass().getName() + " does not have an e-mail field");
		} catch (IllegalArgumentException e) {
			LOGGER.log(Level.WARNING, this.getClass().getName() + e.getMessage());
			View.showMessage("Invalid E-mail", JOptionPane.WARNING_MESSAGE);
			return false;
		}
		return true;
	}

